// Processed candidates mapping using standard party names
export const CANDIDATES: { [seat: string]: { [party: string]: string } } = {};

Object.keys(RAW_CANDIDATES).forEach(seat => {
  CANDIDATES[seat] = {};
  const seatData = RAW_CANDIDATES[seat];
  Object.keys(seatData).forEach(rawParty => {
    const candidateName = seatData[rawParty];
    const stdParty = PARTY_MAP[rawParty] || rawParty;
    CANDIDATES[seat][stdParty] = candidateName;
  });
});
